Snake.prototype.setDirection = function(direction){
        var newX, newY;
        switch(direction){
            case LEFT:
                newX = -1;
                newY = 0;
                break;
            case RIGHT:
                newX = 1;
                newY = 0;
                break;
            case UP:
                newX = 0;
                newY = -1;
                break;
            case DOWN:
                newX = 0;
                newY = 1;
                break;
            default:
                console.error("Unknow direction" + direction);
                return;
        }
        this.newDirectionX = newX;
        this.newDirectionY = newY;
    }

function onGrow(){
    snake.grow();
    var foodIndex = foodAt(snake.head.x,snake.head.y);
    if(foodIndex >= 0){
        food.splice(foodIndex, 1);
        snakeLengthInc++;
        generateFood();
    }
    if(snake.contains(snake.head.x, snake.head.y, true)){
        draw();
        gameOver();
        return;
    }
    if(snakeLengthInc == 0){
        snake.shrink();
    }else{
        snakeLengthInc--;
    }
    draw();
}

function generateFood(){
    var x, y;

    do{
        x = Math.random() * GRID_WIDTH | 0;
        y = Math.random() * GRID_HEIGHT | 0;
        console.log(x, y);
    }while(snake.contains(x, y) || foodAt(x, y) >= 0);
    food.push({x: x, y: y});
}
